import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import './LandingPage.css';

import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const LandingPage = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    // 1. Scene setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(10, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ canvas: canvasRef.current, antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff, 1);

    document.body.appendChild(renderer.domElement);
    document.body.style.height = "500vh";

    // 2. Load Background Image
    const textureLoader = new THREE.TextureLoader();
    const backgroundTexture = textureLoader.load('/assets/background.jpg'); // Replace with your image path
    const backgroundMaterial = new THREE.MeshBasicMaterial({ map: backgroundTexture });

    const backgroundGeometry = new THREE.PlaneGeometry(100, 100);
    const backgroundPlane = new THREE.Mesh(backgroundGeometry, backgroundMaterial);
    backgroundPlane.position.z = -10;
    scene.add(backgroundPlane);

    // 3. Lighting
    const light = new THREE.AmbientLight(0xffffff, 1.5);
    scene.add(light);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(5, 5, 5).normalize();
    scene.add(directionalLight);

    // 4. Load Video Texture
    const video = document.createElement('video');
    video.src = '/assets/intro.mp4';
    video.loop = true;
    video.muted = true;
    video.load();

    const startVideo = () => {
      video.play().catch((error) => {
        console.error('Error attempting to play video:', error);
      });
      window.removeEventListener('click', startVideo);
    };

    window.addEventListener('click', startVideo);

    const videoTexture = new THREE.VideoTexture(video);
    videoTexture.minFilter = THREE.LinearFilter;
    videoTexture.magFilter = THREE.LinearFilter;

    const videoGeometry = new THREE.PlaneGeometry(2, 2);
    const videoMaterial = new THREE.MeshBasicMaterial({ map: videoTexture, side: THREE.DoubleSide });
    const videoPlane = new THREE.Mesh(videoGeometry, videoMaterial);
    videoPlane.scale.set(window.innerWidth / window.innerHeight, 1, 1);
    videoPlane.position.z = -1;
    scene.add(videoPlane);

    // 5. Load GLTF Model
    const loader = new GLTFLoader();
    let propeller = null;

    loader.load('./a.glb', (gltf) => {
      propeller = gltf.scene;
      scene.add(propeller);
      propeller.scale.set(10, 10, 10);
      propeller.position.set(15, 0, 0);
      propeller.rotation.x = Math.PI / 2;
    }, undefined, (error) => {
      console.error("Error loading GLTF model:", error);
    });

    // 6. OrbitControls
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.25;
    controls.maxPolarAngle = Math.PI / 2;
    controls.enableZoom = true;

    // 7. Mouse Movement
    const mouse = new THREE.Vector2();
    const onMouseMove = (event) => {
      mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

      if (propeller) {
        propeller.rotation.x = Math.PI / 2 + mouse.y * Math.PI / 6;
        propeller.rotation.y = mouse.x * Math.PI / 3;
      }
    };

    window.addEventListener('mousemove', onMouseMove);

    // 8. Animate the model
    const animate = () => {
      requestAnimationFrame(animate);

      if (propeller) {
        propeller.rotation.y += 0.05;
        propeller.position.x = window.scrollY / window.innerHeight * 5;
        propeller.position.y = -(window.scrollY / window.innerHeight) * 1;
      }

      const zoomFactor = 10 + window.scrollY / window.innerHeight * 20;
      camera.position.z = zoomFactor;

      controls.update();
      renderer.render(scene, camera);
    };

    animate();
    camera.position.z = 100;

    return () => {
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('click', startVideo);
    };
  }, []);

  return (
    <>
      <canvas ref={canvasRef} />
      <nav style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        background: 'transparent',
        zIndex: 10,
        padding: '10px',
        display: 'flex',
        justifyContent: 'flex-end',
        alignItems: 'center',
      }}>
        <img
          src="logo.png"
          alt="Logo"
          style={{
            position: 'absolute',
            left: '1700px',
            top: '50%',
            transform: 'translateY(-50%)',
            height: '50px',
          }}
        />
      </nav>
      <div className="text-overlay" style={{
        position: 'absolute',
        bottom: '20px',
        left: '75px',
        color: 'white',
        fontWeight: 'bold',
        fontSize: '114px',
        zIndex: 15,
        textShadow: '2px 2px 5px rgba(0, 0, 0, 0.7)',
      }}>
        Electrifying Global Aviation
      </div>
      <div className="text-overlay" style={{
        position: 'absolute',
        bottom: '20px',
        left: '1705px',
        color: 'white',
        fontWeight: 'bold',
        fontSize: '44px',
        zIndex: 15,
        textShadow: '2px 2px 5px rgba(0, 0, 0, 0.7)',
      }}>
        Drag me
      </div>
    </>
  );
};

export default LandingPage;
