import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './KnowMorePage.css';

const KnowMorePage = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const images = ['/assets/i7.png', '/assets/i8.png', '/assets/i9.png', '/assets/i10.png'];

  const goToNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const goToPreviousImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      goToNextImage();
    }, 3000);
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="know-more-page">
      <nav
        style={{
          position: 'absolute',
          top: 10,
          left: 0,
          width: '100%',
          background: 'transparent',
          zIndex: 1,
          padding: '10px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        {/* Navbar content */}
      </nav>

      <div className="nwa-news-text">NWA News and Reports</div>

      <div className="know-more-content">
        <Link to="/" style={{ textDecoration: 'none', fontSize: '18px' }}>
          Go Back to Landing Page
        </Link>
      </div>

      <div className="carousel-container">
        <button className="carousel-button prev" onClick={goToPreviousImage}>
          &#8592;
        </button>
        <div className="carousel-image-container">
          <div className={`carousel-image-wrapper ${currentImageIndex === 0 ? 'slide-left' : 'slide-right'}`}>
            <img src={images[currentImageIndex]} alt="carousel" className="carousel-image" />
          </div>
        </div>
        <button className="carousel-button next" onClick={goToNextImage}>
          &#8594;
        </button>
      </div>

      <footer className="footer">
        <div className="footer-info">
          <p>üìç Jakkur, Bangalore</p>
          <p>üìû +91-XXX-XXX-XXXX</p>
          <p>‚úâÔ∏è info@nauticalwingsaerospace.com</p>
        </div>
        <div className="footer-links">
          <ul>
            <li>Enterprise</li>
            <li>Electric Aviation</li>
            <li>Innovation Centre</li>
            <li>Experience Centre</li>
            <li>Launchpad</li>
            <li>About Us</li>
            <li>Services</li>
            <li>Quality & Safety</li>
          </ul>
        </div>
      </footer>
    </div>
  );
};

export default KnowMorePage;
