import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls'; // Optional: for interactive controls

const LandingPage = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    // 1. Scene setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ canvas: canvasRef.current, antialias: true });

    // Set the renderer size and background color to white
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff, 1);  // Set the background color to white (hex: 0xffffff)
    document.body.appendChild(renderer.domElement);

    // 2. Lighting
    const light = new THREE.AmbientLight(0xffffff, 1.5);  // Ambient light
    scene.add(light);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 1); // Adding directional light
    directionalLight.position.set(5, 5, 5);
    scene.add(directionalLight);

    // 3. Load GLTF Model
    const loader = new GLTFLoader();
    let propeller = null;

    loader.load('/public/a.glb', (gltf) => {
      propeller = gltf.scene;
      scene.add(propeller);
      propeller.scale.set(0.5, 0.5, 0.5);  // Adjust size if necessary
      propeller.position.set(0, 0, 0);   // Make sure the model is in view
    });

    // 4. Camera Position
    camera.position.set(0, 0, 5);  // Position the camera so it can view the model

    // 5. Scroll effect
    let scrollFactor = 0;

    const handleScroll = () => {
      scrollFactor = window.scrollY / window.innerHeight;  // Normalize scroll value
    };

    window.addEventListener('scroll', handleScroll);

    // 6. Animate the model on scroll
    const animate = () => {
      requestAnimationFrame(animate);

      // Zoom effect based on scroll (camera zoom in/out)
      camera.position.z = 5 + scrollFactor * 10;  // Adjust the zoom range as needed

      // Rotate the propeller wings based on scroll
      if (propeller) {
        propeller.rotation.y += 0.01 + scrollFactor * 0.05;  // Rotation speed, adjust as needed
      }

      renderer.render(scene, camera);
    };

    animate();

    // Optional: Adding OrbitControls for camera movement and to help you view the model
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.25;
    controls.enableZoom = true;

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
      controls.dispose();  // Clean up OrbitControls
    };
  }, []);

  return <canvas ref={canvasRef} />;
};

export default LandingPage;
