import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './KnowMorePage.css';

const KnowMorePage = () => {
  const [tilt, setTilt] = useState({ x: 0, y: 0 });
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = ['/assets/i7.png', '/assets/i8.png'];
  const images1 = ['/assets/i9.png', '/assets/i10.png'];

  const handleMouseMove = (e) => {
    const card = e.currentTarget;
    const { offsetWidth: width, offsetHeight: height } = card;
    const { offsetX: x, offsetY: y } = e.nativeEvent;

    const xTilt = (y / height - 0.5) * 30;
    const yTilt = (x / width - 0.5) * -30;

    setTilt({ x: xTilt, y: yTilt });
  };

  const goToNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const goToPreviousImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  useEffect(() => {
    const intervalId = setInterval(goToNextImage, 2000);
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="know-more-page">
      <nav style={{ position: 'absolute', top: 10, width: '100%', background: 'transparent', padding: '10px' }}>
        {/* Navbar content */}
      </nav>
      <div className="nwa-news-text block">NWA News and Reports</div>
      <div className="know-more-content block">
        <Link to="/" style={{ textDecoration: 'none', fontSize: '18px' }}>
          Go Back to Landing Page
        </Link>
      </div>

      <div className="image-cards block">
        {[...Array(3)].map((_, index) => (
          <div
            key={index}
            className="card"
            onMouseMove={handleMouseMove}
            style={{ transform: `rotateX(${tilt.x}deg) rotateY(${tilt.y}deg)` }}
          >
            <img src={`/assets/i${index + 1}.png`} alt={`Card ${index + 1}`} className="card-img" />
            <div className="card-content">
              <h2>Image {index + 1}</h2>
            </div>
          </div>
        ))}
      </div>

      <div className="carousel-container block">
        <button className="carousel-button prev" onClick={goToPreviousImage}>&#8592;</button>
        <div className="carousel-image-container">
          <img src={images[currentImageIndex]} alt="carousel" className="carousel-image" />
        </div>
        <button className="carousel-button next" onClick={goToNextImage}>&#8594;</button>
      </div>

      <footer className="footer">
        <div className="footer-info">
          <p>üìç Jakkur, Bangalore</p>
          <p>üìû +91-XXX-XXX-XXXX</p>
          <p>‚úâÔ∏è info@nauticalwingsaerospace.com</p>
        </div>
        <div className="footer-links">
          <ul>
            <li>Enterprise</li>
            <li>Electric Aviation</li>
            <li>Innovation Centre</li>
          </ul>
        </div>
      </footer>
    </div>
  );
};

export default KnowMorePage;
