import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import './LandingPage.css';

import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const LandingPage = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    // 1. Scene setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(10, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ canvas: canvasRef.current, antialias: true });

    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff, 1);

    document.body.appendChild(renderer.domElement);
    document.body.style.height = "500vh";

    // 2. Lighting
    const light = new THREE.AmbientLight(0xffffff, 1.5);
    scene.add(light);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(5, 5, 5).normalize();
    scene.add(directionalLight);

    // 3. Load Video Texture for Background
    const video = document.createElement('video');
    video.src = '/assets/intro.mp4'; // Path to your video file
    video.loop = true;
    video.muted = true; // Mute video for autoplay
    video.autoplay = true; // Enable autoplay
    video.playsInline = true; // Avoid full-screen behavior on mobile
    video.load();
    video.play().catch((error) => {
      console.error('Autoplay failed:', error);
    });

    // Create a video texture
    const videoTexture = new THREE.VideoTexture(video);
    videoTexture.minFilter = THREE.LinearFilter;
    videoTexture.magFilter = THREE.LinearFilter;

    const geometry = new THREE.PlaneGeometry(2, 2);
    const material = new THREE.MeshBasicMaterial({ map: videoTexture, side: THREE.DoubleSide });
    const plane = new THREE.Mesh(geometry, material);
    plane.scale.set(window.innerWidth / window.innerHeight, 1, 1);
    plane.position.z = -1;
    scene.add(plane);

    // 4. Load GLTF Model
    const loader = new GLTFLoader();
    let propeller = null;

    loader.load('./a.glb', (gltf) => {
      propeller = gltf.scene;
      scene.add(propeller);
      propeller.scale.set(10, 10, 10);
      propeller.position.set(15, 0, 0);
      propeller.rotation.x = Math.PI / 2;
    }, undefined, (error) => {
      console.error("Error loading GLTF model:", error);
    });

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.25;
    controls.maxPolarAngle = Math.PI / 2;
    controls.enableZoom = true;

    const animate = () => {
      requestAnimationFrame(animate);
      if (propeller) propeller.rotation.y += 0.05;
      camera.position.z = 10 + window.scrollY / window.innerHeight * 20;
      if (propeller) {
        propeller.position.x = window.scrollY / window.innerHeight * 5;
        propeller.position.y = -(window.scrollY / window.innerHeight) * 1;
      }
      controls.update();
      renderer.render(scene, camera);
    };

    animate();
    camera.position.z = 100;

    return () => {
      renderer.dispose();
    };
  }, []);

  return (
    <>
      <canvas ref={canvasRef} />
      <nav style={{ position: 'absolute', top: 0, left: 0, right: 0, background: 'transparent', zIndex: 10 }}>
        <img src="logo.png" alt="Logo" style={{ height: '50px' }} />
      </nav>
      <div className="text-overlay" style={{ position: 'absolute', bottom: '20px', left: '75px', color: 'white', fontSize: '114px', zIndex: 15 }}>
        Electrifying Global Aviation
      </div>
    </>
  );
};

export default LandingPage;
