import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './KnowMorePage.css';

const KnowMorePage = () => {
  const [tilt, setTilt] = useState({ x: 0, y: 0 });
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = ['/assets/i7.png', '/assets/i8.png'];
  const images1 = ['/assets/i9.png', '/assets/i10.png'];

  const handleMouseMove = (e) => {
    const card = e.currentTarget;
    const { offsetWidth: width, offsetHeight: height } = card;
    const { offsetX: x, offsetY: y } = e.nativeEvent;

    const xTilt = (y / height - 0.5) * 30;
    const yTilt = (x / width - 0.5) * -30;

    setTilt({ x: xTilt, y: yTilt });
  };

  const goToNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const goToPreviousImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  useEffect(() => {
    const intervalId = setInterval(goToNextImage, 2000);
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="know-more-page">
      <nav>
        {/* Navbar */}
      </nav>

      <div className="nwa-news-text">NWA News and Reports</div>

      <div className="know-more-content">
        <Link to="/" style={{ textDecoration: 'none', fontSize: '18px' }}>
          Go Back to Landing Page
        </Link>
      </div>

      <div className="image-cards">
        {/* Image Cards */}
      </div>

      <div className="electric-aviation-text">
        <h1 className="main-heading">Pioneering Electric Aviation</h1>
        <h2 className="sub-heading">
          We're India's 1st Electric Propulsion Technology Company.
        </h2>
        <p className="mission-paragraph">
          Our mission is to contribute to the global effort of sustainable aviation by developing
          advanced electric propulsion systems for aircraft & unmanned systems.
        </p>
      </div>

      <div className="carousel-container">
        <button className="carousel-button prev" onClick={goToPreviousImage}>
          &#8592;
        </button>
        <div className="carousel-image-container">
          <img src={images[currentImageIndex]} alt="carousel" className="carousel-image" />
        </div>
        <button className="carousel-button next" onClick={goToNextImage}>
          &#8594;
        </button>
      </div>

      <div className="electric-aviation-text">
        <h3 className="sub-heading">An electric propulsion technology for the aviation industry.</h3>
        <h2 className="main-heading">Fly Beyond Excellence</h2>
      </div>

      <div className="electric-aviation-text">
        <h1 className="main-heading">Meet Our People</h1>
      </div>

      <div className="carousel-image-container">
        <img src={images1[currentImageIndex]} alt="carousel" className="carousel-image" />
      </div>

      <h2 className="main-heading">Careers</h2>

      {/* New Image Below Careers */}
      <div className="career-image-container">
        <img src="i11.jpg" alt="Careers" className="career-image" />
      </div>
    </div>
  );
};

export default KnowMorePage;
