import React, { useRef } from 'react';
import { Link } from 'react-router-dom'; // Import Link for routing
import './KnowMorePage.css'; // Import the CSS for this page

const KnowMorePage = () => {
  const cardRefs = useRef([]);

  // Function to handle the mouse move for cursor-following effect
  const handleMouseMove = (event) => {
    cardRefs.current.forEach((card) => {
      const cardWidth = card.offsetWidth;
      const cardHeight = card.offsetHeight;
      const centerX = cardWidth / 2;
      const centerY = cardHeight / 2;
      const mouseX = event.clientX - card.offsetLeft;
      const mouseY = event.clientY - card.offsetTop;
      const deltaX = mouseX - centerX;
      const deltaY = mouseY - centerY;
      const rotationX = (deltaY / cardHeight) * 10; // Tilt value for X-axis
      const rotationY = (deltaX / cardWidth) * -10; // Tilt value for Y-axis

      card.style.transform = `scale(1.1) perspective(1500px) rotateX(${rotationX}deg) rotateY(${rotationY}deg)`; 
    });
  };

  const handleMouseLeave = () => {
    cardRefs.current.forEach((card) => {
      card.style.transform = 'scale(1)'; // Reset scale and tilt when mouse leaves
    });
  };

  return (
    <div className="know-more-page">
      <nav
        style={{
          position: 'absolute',
          top: 10,
          left: 0,
          width: '100%',
          background: 'transparent',
          zIndex: 1,
          padding: '10px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        {/* Navbar content */}
      </nav>

      <div className="nwa-news-text">
        NWA News and Reports
      </div>

      <div className="know-more-content">
        <h2>Welcome to the NWA Knowledge Portal</h2>
        <Link to="/" style={{ textDecoration: 'none', fontSize: '18px' }}>
          Go Back to Landing Page
        </Link>
      </div>

      {/* Image Cards Section */}
      <div className="image-cards">
        <div
          className="card"
          ref={(el) => cardRefs.current.push(el)}
          onMouseMove={handleMouseMove}
          onMouseLeave={handleMouseLeave}
        >
          <img src="/assets/i1.png" alt="Aviation News" className="card-img" />
          <div className="card-content">
            <h2>Image 1</h2>
          </div>
        </div>
        <div
          className="card"
          ref={(el) => cardRefs.current.push(el)}
          onMouseMove={handleMouseMove}
          onMouseLeave={handleMouseLeave}
        >
          <img src="/assets/i2.png" alt="Industry Trends" className="card-img" />
          <div className="card-content">
            <h2>Image 2</h2>
          </div>
        </div>
        <div
          className="card"
          ref={(el) => cardRefs.current.push(el)}
          onMouseMove={handleMouseMove}
          onMouseLeave={handleMouseLeave}
        >
          <img src="/assets/i3.png" alt="Reports" className="card-img" />
          <div className="card-content">
            <h2>Image 3</h2>
          </div>
        </div>
      </div>
    </div>
  );
};

export default KnowMorePage;
