import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link for routing
import './KnowMorePage.css'; // Import the CSS for this page

const KnowMorePage = () => {
  const [tilt, setTilt] = useState({ x: 0, y: 0 });

  // Function to handle mouse movement inside the card
  const handleMouseMove = (e) => {
    const card = e.currentTarget;
    const { offsetWidth: width, offsetHeight: height } = card;
    const { offsetX: x, offsetY: y } = e.nativeEvent;

    // Increase the factor for stronger tilt effect
    const xTilt = (y / height - 0.5) * 30; // Increased factor for stronger tilt in X direction
    const yTilt = (x / width - 0.5) * -30; // Increased factor for stronger tilt in Y direction

    setTilt({ x: xTilt, y: yTilt });
  };

  return (
    <div className="know-more-page">
      <nav
        style={{
          position: 'absolute',
          top: 10,
          left: 0,
          width: '100%',
          background: 'transparent',
          zIndex: 1,
          padding: '10px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
        }}
      >
        {/* Navbar content */}
      </nav>

      <div className="nwa-news-text">
        NWA News and Reports
      </div>

      <div className="know-more-content">
        <Link to="/" style={{ textDecoration: 'none', fontSize: '18px' }}>
          Go Back to Landing Page
        </Link>
      </div>

      {/* Image Cards Section */}
      <div className="image-cards">
        <div
          className="card"
          onMouseMove={handleMouseMove} // Track mouse movement
          style={{
            transform: `rotateX(${tilt.x}deg) rotateY(${tilt.y}deg)`, // Apply tilt effect
          }}
        >
          <img src="/assets/i1.png" alt="Aviation News" className="card-img" />
          <div className="card-content">
            <h2>Image 1</h2>
          </div>
        </div>
        <div
          className="card"
          onMouseMove={handleMouseMove} // Track mouse movement
          style={{
            transform: `rotateX(${tilt.x}deg) rotateY(${tilt.y}deg)`, // Apply tilt effect
          }}
        >
          <img src="/assets/i2.png" alt="Industry Trends" className="card-img" />
          <div className="card-content">
            <h2>Image 2</h2>
          </div>
        </div>
        <div
          className="card"
          onMouseMove={handleMouseMove} // Track mouse movement
          style={{
            transform: `rotateX(${tilt.x}deg) rotateY(${tilt.y}deg)`, // Apply tilt effect
          }}
        >
          <img src="/assets/i3.png" alt="Reports" className="card-img" />
          <div className="card-content">
            <h2>Image 3</h2>
          </div>
        </div>
      </div>
    </div>
  );
};

export default KnowMorePage;
