import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import './LandingPage.css';

import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const LandingPage = () => {
  const videoCanvasRef = useRef(null);
  const propellerCanvasRef = useRef(null);

  useEffect(() => {
    // Video Scene Setup
    const videoScene = new THREE.Scene();
    const videoCamera = new THREE.PerspectiveCamera(10, window.innerWidth / window.innerHeight, 0.1, 1000);
    const videoRenderer = new THREE.WebGLRenderer({ canvas: videoCanvasRef.current, antialias: true });

    videoRenderer.setSize(window.innerWidth, window.innerHeight);
    videoRenderer.setClearColor(0xffffff, 1);

    // Add Video Background
    const video = document.createElement('video');
    video.src = '/assets/intro.mp4';
    video.loop = true;
    video.muted = true;
    video.load();

    const startVideo = () => {
      video.play().catch((error) => console.error('Error playing video:', error));
      window.removeEventListener('click', startVideo);
    };
    window.addEventListener('click', startVideo);

    const videoTexture = new THREE.VideoTexture(video);
    const videoPlane = new THREE.Mesh(
      new THREE.PlaneGeometry(2, 2),
      new THREE.MeshBasicMaterial({ map: videoTexture, side: THREE.DoubleSide })
    );
    videoPlane.scale.set(window.innerWidth / window.innerHeight, 1, 1);
    videoPlane.position.z = -1;
    videoScene.add(videoPlane);

    const videoAnimate = () => {
      requestAnimationFrame(videoAnimate);
      videoCamera.position.z = 10 + window.scrollY / window.innerHeight * 20;
      videoRenderer.render(videoScene, videoCamera);
    };
    videoAnimate();

    // Propeller Scene Setup
    const propellerScene = new THREE.Scene();
    const propellerCamera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const propellerRenderer = new THREE.WebGLRenderer({ canvas: propellerCanvasRef.current, antialias: true });

    propellerRenderer.setSize(window.innerWidth, window.innerHeight);
    propellerRenderer.setClearColor(0xffffff, 1);

    // Lighting for the Propeller
    const propellerLight = new THREE.AmbientLight(0xffffff, 1);
    propellerScene.add(propellerLight);

    let propeller = null;
    const loader = new GLTFLoader();
    loader.load('./a.glb', (gltf) => {
      propeller = gltf.scene;
      propellerScene.add(propeller);

      propeller.scale.set(10, 10, 10);
      propeller.position.set(0, 0, 0);
    });

    const propellerAnimate = () => {
      requestAnimationFrame(propellerAnimate);
      if (propeller) {
        propeller.rotation.y += 0.05; // Rotate propeller
      }
      propellerRenderer.render(propellerScene, propellerCamera);
    };

    propellerCamera.position.z = 50; // Set initial camera position
    propellerAnimate();

    return () => {
      window.removeEventListener('click', startVideo);
    };
  }, []);

  return (
    <>
      {/* Video Background */}
      <canvas ref={videoCanvasRef} />
      <nav
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          background: 'transparent',
          zIndex: 10,
          padding: '10px',
        }}
      >
        <h1 style={{ color: 'white' }}></h1>
      </nav>
      <div
        className="text-overlay"
        style={{
          position: 'absolute',
          bottom: '20px',
          left: '75px',
          color: 'white',
          fontWeight: 'bold',
          fontSize: '114px',
          zIndex: 15,
          textShadow: '2px 2px 5px rgba(0, 0, 0, 0.7)',
        }}
      >
        Electrifying Global Aviation
      </div>

      {/* Spacer Section */}
      <div
        style={{
          height: '100vh',
          background: '#f0f0f0',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          fontSize: '32px',
          fontWeight: 'bold',
          color: '#333',
        }}
      >
        Scroll Down for the Propeller
      </div>

      {/* Propeller Section */}
      <div style={{ height: '100vh', position: 'relative' }}>
        <canvas ref={propellerCanvasRef} />
      </div>
    </>
  );
};

export default LandingPage;
