import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';


const LandingPage = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    // Scene setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ canvas: canvasRef.current, antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    // Lighting
    const light = new THREE.AmbientLight(0xffffff, 1.5);
    scene.add(light);

    // GLTF Model Loader
    const loader = new GLTFLoader();
    let propeller = null;

    loader.load('./a.glb', (gltf) => {
      propeller = gltf.scene;
      scene.add(propeller);
      propeller.scale.set(0.5, 0.5, 0.5);  // Adjust size if needed
    });

    // Scroll effect
    let scrollFactor = 0;

    const handleScroll = () => {
      scrollFactor = window.scrollY / window.innerHeight;  // Normalize scroll value
    };

    window.addEventListener('scroll', handleScroll);

    // Animate the model on scroll
    const animate = () => {
      requestAnimationFrame(animate);

      // Zoom effect based on scroll
      camera.position.z = 5 + scrollFactor * 5;

      // Rotate propeller model based on scroll
      if (propeller) {
        propeller.rotation.y += 0.01 + scrollFactor * 0.05;  // Rotate speed based on scroll
      }

      renderer.render(scene, camera);
    };

    animate();

    // Clean up on unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return <canvas ref={canvasRef} />;
};

export default LandingPage;
