import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const LandingPage = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    // 1. Scene setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(25, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ canvas: canvasRef.current, antialias: true });

    // Set the renderer size and background color to white
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff, 1);  // Set the background color to white

    // Append the canvas to the body
    document.body.appendChild(renderer.domElement);

    // Set a height greater than the viewport to allow scrolling
    document.body.style.height = "500vh"; // 5 times the height of the viewport

    // 2. Lighting
    const light = new THREE.AmbientLight(0xffffff, 1.5);  // Ambient light
    scene.add(light);

    // Add a directional light to illuminate the model better
    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(5, 5, 5).normalize();
    scene.add(directionalLight);

    // 3. Load GLTF Model
    const loader = new GLTFLoader();
    let propeller = null;

    loader.load('./a.glb', (gltf) => {
      propeller = gltf.scene;
      scene.add(propeller);

      // Scale and position the propeller for visibility
      propeller.scale.set(10, 10, 10);  // Make it larger (adjust as needed)

      // Place the propeller in front of the camera, focus on one blade
      propeller.position.set(0, 0, 0);  // Place it at the center of the scene

      // Rotate the propeller to make it vertical and facing the camera
      propeller.rotation.x = Math.PI / 2; // Rotate 90 degrees along the X axis
      propeller.rotation.y = 0;  // Reset any rotation on the Y axis

      console.log("Model loaded successfully!");
    }, undefined, (error) => {
      console.error("Error loading GLTF model:", error);
    });

    // 4. Add OrbitControls to make the scene interactive
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.25;
    controls.screenSpacePanning = false;
    controls.maxPolarAngle = Math.PI / 2; // Prevent the user from rotating upside down
    controls.enableZoom = true;

    // 5. Track Mouse Movement for Propeller Rotation and Dragging
    const mouse = new THREE.Vector2(); // Mouse position
    const onMouseMove = (event) => {
      // Normalize mouse position ([-1, 1]) based on window dimensions
      mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
    };

    let isMouseDown = false;
    const onMouseDown = () => {
      isMouseDown = true;
    };

    const onMouseUp = () => {
      isMouseDown = false;
    };

    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mousedown', onMouseDown);
    window.addEventListener('mouseup', onMouseUp);

    // 6. Animate the model with continuous rotation and tilt based on mouse drag
    const animate = () => {
      requestAnimationFrame(animate);

      // Continuous rotation of the propeller
      if (propeller) {
        propeller.rotation.z += 0.01;  // Keep rotating the propeller
      }

      // Zoom effect based on scroll (camera zoom in/out)
      const zoomFactor = 5 + window.scrollY / window.innerHeight * 20;  // Zoom range, adjust as needed
      camera.position.z = zoomFactor;

      // Move the propeller and rotate it based on scroll
      if (propeller) {
        // Adjust the rotation of the propeller based on mouse position
        if (isMouseDown) {
          // When mouse is being dragged, tilt the propeller without scaling
          propeller.rotation.x = Math.PI / 2 + mouse.y * Math.PI / 6;  // Tilt around X axis
          propeller.rotation.y = mouse.x * Math.PI / 3;  // Rotate around Y axis
        }

        // Move the propeller to the side based on scroll
        propeller.position.x = window.scrollY / window.innerHeight * 5;  // Move to the side on scroll
        propeller.position.y = -(window.scrollY / window.innerHeight) * 1;  // Move slightly downward
      }

      // Update OrbitControls
      controls.update();

      renderer.render(scene, camera);
    };

    animate();

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('mousedown', onMouseDown);
      window.removeEventListener('mouseup', onMouseUp);
    };
  }, []);

  return <canvas ref={canvasRef} />;
};

export default LandingPage;
