import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const LandingPage = () => {
  const canvasRef = useRef(null);
  const textRef = useRef(null);

  useEffect(() => {
    // ... THREE.js setup code remains unchanged ...

    // 3D Text Tilt Effect
    const handleMouseMove = (event) => {
      if (textRef.current) {
        const rect = textRef.current.getBoundingClientRect();
        const centerX = rect.left + rect.width / 2;
        const centerY = rect.top + rect.height / 2;

        const deltaX = event.clientX - centerX;
        const deltaY = event.clientY - centerY;

        const rotationX = (deltaY / rect.height) * 15; // Adjust tilt strength
        const rotationY = (deltaX / rect.width) * -15;

        textRef.current.style.transform = `perspective(1000px) rotateX(${rotationX}deg) rotateY(${rotationY}deg)`;
      }
    };

    const resetTilt = () => {
      if (textRef.current) {
        textRef.current.style.transform = `perspective(1000px) rotateX(0deg) rotateY(0deg)`;
      }
    };

    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseout', resetTilt);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseout', resetTilt);
    };
  }, []);

  return (
    <>
      <canvas ref={canvasRef} />
      {/* Navbar */}
      <nav
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          background: 'transparent',
          zIndex: 10, // Ensures the navbar is above the video
          padding: '10px',
        }}
      >
        <h1 style={{ color: 'white' }}></h1>
      </nav>
      {/* Text Overlay */}
      <div
        ref={textRef}
        style={{
          position: 'absolute',
          bottom: '20px',
          left: '20px',
          color: 'white',
          fontWeight: 'bold',
          fontSize: '144px',
          zIndex: 15,
          textShadow: '2px 2px 5px rgba(0, 0, 0, 0.7)',
          transition: 'transform 0.1s ease-out', // Smooth transition for tilt
          willChange: 'transform', // Optimize for performance
        }}
      >
        Electrifying Global Aviation
      </div>
    </>
  );
};

export default LandingPage;
