import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { Link } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    // Scene and Camera Setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(10, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.z = 100;

    const renderer = new THREE.WebGLRenderer({ canvas: canvasRef.current, antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setClearColor(0xffffff, 1);

    document.body.style.height = '500vh';

    // Lighting Setup
    const ambientLight = new THREE.AmbientLight(0xffffff, 1.5);
    scene.add(ambientLight);
    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(5, 5, 5).normalize();
    scene.add(directionalLight);

    // Video Background
    const video = document.createElement('video');
    video.src = '/assets/intro.mp4';
    video.loop = true;
    video.muted = true;
    video.load();

    const startVideo = () => video.play().catch(console.error);
    window.addEventListener('click', startVideo);

    const videoTexture = new THREE.VideoTexture(video);
    const plane = new THREE.Mesh(
      new THREE.PlaneGeometry(2, 2),
      new THREE.MeshBasicMaterial({ map: videoTexture })
    );
    plane.scale.set(window.innerWidth / window.innerHeight, 1, 1);
    plane.position.z = -1;
    scene.add(plane);

    // GLTF Model Loader
    const loader = new GLTFLoader();
    let propeller = null;

    loader.load(
      './a.glb',
      (gltf) => {
        propeller = gltf.scene;
        propeller.scale.set(10, 10, 10);
        propeller.position.set(15, 0, 0);
        propeller.rotation.x = Math.PI / 2;
        scene.add(propeller);
      },
      undefined,
      (error) => console.error('GLTF loading error:', error)
    );

    // Orbit Controls
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.25;
    controls.maxPolarAngle = Math.PI / 2;
    controls.enablePan = false;

    // Mouse Interaction for Propeller
    const mouse = new THREE.Vector2();
    const onMouseMove = (event) => {
      mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
      if (propeller) {
        propeller.rotation.x = Math.PI / 2 + THREE.MathUtils.clamp(mouse.y * Math.PI / 6, -Math.PI / 6, Math.PI / 6);
        propeller.rotation.y = THREE.MathUtils.clamp(mouse.x * Math.PI / 3, -Math.PI / 3, Math.PI / 3);
      }
    };

    window.addEventListener('mousemove', onMouseMove);

    // Animation Loop
    const animate = () => {
      requestAnimationFrame(animate);
      if (propeller) propeller.rotation.y += 0.05;
      camera.position.z = 10 + (window.scrollY / window.innerHeight) * 20;
      controls.update();
      renderer.render(scene, camera);
    };

    animate();

    return () => {
      window.removeEventListener('click', startVideo);
      window.removeEventListener('mousemove', onMouseMove);
      renderer.dispose();
    };
  }, []);

  return (
    <>
      <canvas ref={canvasRef} />
      <nav className="navbar">
        {/* Navigation bar or links can be added here */}
      </nav>
      <img src="logo.png" alt="Logo" className="logo" />
      <div className="text-overlay-main">Electrifying Global Aviation</div>
      <div className="text-overlay-propeller">Drag the propeller to view in 3D</div>
      <div className="text-overlay-link">
        <Link to="/know-more">Click here to know more</Link>
      </div>
    </>
  );
};

export default LandingPage;
