import React, { useEffect } from 'react';
import { Canvas } from '@react-three/fiber';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import gsap from 'gsap';
import './Navbar.css';

import VideoPlane from '../threejs/VideoPlane';

gsap.registerPlugin(ScrollTrigger);

const LandingPage = () => {
  useEffect(() => {
    const video = document.querySelector('video');

    if (video) {
      const duration = video.duration || 1; // Prevent divide-by-zero errors
      const scrollTrigger = ScrollTrigger.create({
        trigger: '#canvas-container',
        start: 'top top',
        end: 'bottom bottom',
        scrub: true,
        onUpdate: (self) => {
          video.currentTime = self.progress * duration; // Update video based on scroll
        },
      });

      return () => scrollTrigger.kill(); // Cleanup
    }
  }, []);

  return (
    <div id="canvas-container" style={{ height: '200vh' }}>
      <Canvas>
        <VideoPlane />
      </Canvas>
    </div>
  );
};

export default LandingPage;
